answer = ValidateRequestType();

  function ValidateRequestType() {
     if ( (current.request.x_ibmg3_hcms_broker_request_type == '2') || (current.request.x_ibmg3_hcms_broker_request_type == '3') ) {
        return 'yes';
     }
     return 'no';
  }

try {
    var gr_task = new GlideRecord("sc_request");
    gr_task.addQuery('sys_id', current.request);    
    gr_task.query();
    if (gr_task.next()) {
		if(gr_task.x_ibmg3_hcms_requestapprovalhold == true){
		answer = false;
		}        
		else
			answer = true;
    } else {
        answer = true;
    }
} catch (err) {
    workflow.info("Broker Requested Item Workflow: Wait For Approval to unhold:  " + this.type + "(" + err.message + ")");
}

