try {
    var gr_task = new GlideRecord("sc_task");
    gr_task.addQuery('request_item', current.sys_id);
    gr_task.addQuery('state', '!=', '3');
    gr_task.addQuery('state', '!=', '4');
    gr_task.addQuery('state', '!=', '7');
    gr_task.addQuery('wf_activity', '');
    gr_task.query();
    if (gr_task.next()) {
        //tasks found in other states than 'Closed complete', 'Closed incomplete', or 'Closed Skipped'
        answer = false;
    } else {
        //no tasks found in states other than 'Closed complete', 'Closed incomplete', or 'Closed Skipped'
        answer = true;
    }
} catch (err) {
    workflow.info("Broker Requested Item Workflow: Wait For Task Completion:  " + this.type + "(" + err.message + ")");
}

