answer = ifcartdisabled();

function ifcartdisabled() {
    try {
        //property is set to true if cart is enabled and approval is driven by request
        var cart = gs.getProperty('x_ibmg3_hcms.mvp3.cart.enabled');
        if (cart == "false") {
            return 'yes';
        } else
            return 'no';
    } catch (e) {
        workflow.error("HCMS Provision Workflow: Check if cart is enabled and approval is driven by request : " + this.type + " " + e);
    }
}

